mapper.insert:
insert into dedup_check_detail (id, tran_id, approve_time, 
  cstatus, creason, checker, 
  check_time, update_time, parent_id, grand_parent_id
  )
values (#{id,jdbcType=BIGINT}, #{tranId,jdbcType=BIGINT}, #{approveTime,jdbcType=TIMESTAMP}, 
  #{cstatus,jdbcType=INTEGER}, #{creason,jdbcType=VARCHAR}, #{checker,jdbcType=VARCHAR}, 
  #{checkTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{parentId,jdbcType=BIGINT}, #{grandParentId,jdbcType=BIGINT}
  )

mapper.select:
select

,user_info.user_key,user_info.user_name
from dedup_check_detail left join dedup_mark on dedup_check_detail.tran_id = dedup_mark.id
left join user_info on dedup_mark.operator=user_info.user_key


		dedup_check_detail.tran_id = #{questionId,jdbcType=BIGINT}


		AND (tran_ops.operator_name = #{teacher,jdbcType=VARCHAR} or tran_ops.content_operator_name = #{teacher,jdbcType=VARCHAR})


		AND cstatus = #{status,jdbcType=INTEGER}


		AND (tran_ops.operator_team_id = #{teamid,jdbcType=INTEGER} or tran_ops.content_operator_team_id = #{teamid,jdbcType=INTEGER})


		AND dedup_check_detail.parent_id = #{parent_id,jdbcType=BIGINT}


		AND dedup_check_detail.grand_parent_id = #{grand_parent_id,jdbcType=BIGINT}


order by cstatus asc , check_time desc, id asc
limit #{offset},#{limit};

select
count(1)
from dedup_check_detail


		dedup_check_detail.tran_id = #{questionId,jdbcType=BIGINT}


		AND (tran_ops.operator_name = #{teacher,jdbcType=VARCHAR} or tran_ops.content_operator_name = #{teacher,jdbcType=VARCHAR})


		AND cstatus = #{status,jdbcType=INTEGER}


		AND (tran_ops.operator_team_id = #{teamid,jdbcType=INTEGER} or tran_ops.content_operator_team_id = #{teamid,jdbcType=INTEGER})


		AND dedup_check_detail.parent_id = #{parent_id,jdbcType=BIGINT}


		AND dedup_check_detail.grand_parent_id = #{grand_parent_id,jdbcType=BIGINT}

select count(1) from dedup_check_detail
parent_id = #{parentId,jdbcType=BIGINT}
AND cstatus = 1

select count(1) from dedup_check_detail
parent_id = #{parentId,jdbcType=BIGINT}
AND cstatus = 2

select count(1) from dedup_check_detail
parent_id = #{parentId,jdbcType=BIGINT}
AND cstatus = 0
